import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {
  constructor() {
    super();
    // console.log("hello i am constructor");
    this.state = {
      articles: [],
      loading: false,
      page: 1
    };
  }
  async componentDidMount() {
    // console.log("cdm");
    let url =
      "https://newsapi.org/v2/top-headlines?country=in&apikey=dbe57b028aeb41e285a226a94865f7a7&page=1";
    let data = await fetch(url);
    let parsedData = await data.json();
    // console.log(parsedData);
    this.setState({ articles: parsedData.articles });
  }
   handleNextClick = async ()=>{
    let url =
    `https://newsapi.org/v2/top-headlines?country=in&apikey=dbe57b028aeb41e285a226a94865f7a7&page=${this.sta}`;
  let data = await fetch(url);
  let parsedData = await data.json();
  // console.log(parsedData);
  this.setState({ articles: parsedData.articles });
      this.setState({
          page: this.state.page + 1
      })
      
  }


  render() {
    return (
      <div className="container my-4">
        <h2> News Hand - Top Headlines</h2>
        <div className="row ">
          {this.state.articles.map((element) => {
            return (
              <div className=" col-md-4" key={element.url}>
                <NewsItem
                  title={element.title}
                  description={element.description}
                  imageUrl={element.urlToImage}
                  newsUrl={element.url}
                />
              </div>
            );
          })}
        </div>
        <div className="d-flex justify-content-between my-3">
          <button type="button" disabled={this.state.page<=1} class="btn btn-dark"  onClick="{this.handlePervClick}">
            &larr; Previous
          </button>
          <button type="button" class="btn btn-dark" onClick="{this.handleNextClick}">
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
